export function loadStudentDashboard(n={}){const t=document.querySelector("main");if(!t)return void console.error("Main element not found");t.innerHTML="";const{name:e="John Doe",grade:a="Grade 10",subjects:r=["Mathematics","Science","History"],attendance:d="85%",performance:s={Mathematics:"A",Science:"B",History:"A"}}=n;t.innerHTML=`\n        <div class="container mt-200">\n            <h1 class="text-center">Welcome, ${e}!</h1>\n            <div class="card mt-200">\n                <div class="card-header bg-primary text-white text-center">\n                    Student Dashboard\n                </div>\n                <div class="card-body">\n                    <p><strong>Grade:</strong> ${a}</p>\n                    <p><strong>Attendance:</strong> ${d}</p>\n                    <div>\n                        <h5>Subjects:</h5>\n                        <ul class="list-group">\n                            ${r.map((n=>`<li class="list-group-item">${n}</li>`)).join("")}\n                        </ul>\n                    </div>\n                    <div class="mt-4">\n                        <h5>Performance:</h5>\n                        <table class="table table-striped">\n                            <thead>\n                                <tr>\n                                    <th>Subject</th>\n                                    <th>Grade</th>\n                                </tr>\n                            </thead>\n                            <tbody>\n                                ${Object.entries(s).map((([n,t])=>`\n                                    <tr>\n                                        <td>${n}</td>\n                                        <td>${t}</td>\n                                    </tr>\n                                `)).join("")}\n                            </tbody>\n                        </table>\n                    </div>\n                </div>\n            </div>\n        </div>\n    `}